import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion, useScroll, useTransform } from 'framer-motion';

const Home = () => {
  const [scrollProgress, setScrollProgress] = useState(0);
  const [text, setText] = useState('');
  const fullText = 'Crafting Stories Through Code & Design';
  
  const { scrollY } = useScroll();
  const y1 = useTransform(scrollY, [0, 300], [0, 100]);
  const y2 = useTransform(scrollY, [0, 300], [0, -100]);
  const opacity = useTransform(scrollY, [0, 300], [1, 0]);

  // Typing Animation
  useEffect(() => {
    let index = 0;
    const timer = setInterval(() => {
      if (index <= fullText.length) {
        setText(fullText.slice(0, index));
        index++;
      } else {
        clearInterval(timer);
      }
    }, 100);
    return () => clearInterval(timer);
  }, []);

  // Scroll Progress
  useEffect(() => {
    const handleScroll = () => {
      const totalHeight = document.documentElement.scrollHeight - window.innerHeight;
      const progress = (window.scrollY / totalHeight) * 100;
      setScrollProgress(progress);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Animation variants
  const fadeInUp = {
    hidden: { opacity: 0, y: 60 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } }
  };

  const fadeInLeft = {
    hidden: { opacity: 0, x: -60 },
    visible: { opacity: 1, x: 0, transition: { duration: 0.6, ease: "easeOut" } }
  };

  const fadeInRight = {
    hidden: { opacity: 0, x: 60 },
    visible: { opacity: 1, x: 0, transition: { duration: 0.6, ease: "easeOut" } }
  };

  const scaleIn = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: { opacity: 1, scale: 1, transition: { duration: 0.5, ease: "easeOut" } }
  };

  return (
    <div className="home-page">
      {/* Scroll Progress Bar */}
      <motion.div 
        className="scroll-progress-bar" 
        style={{ width: `${scrollProgress}%` }}
      />

      {/* Hero Section with 3D Avatar */}
      <section className="hero-section">
        <div className="container">
          <div className="hero-grid">
            {/* Left Content */}
            <motion.div 
              className="hero-content"
              initial="hidden"
              animate="visible"
              variants={fadeInLeft}
            >
              <motion.div 
                className="hero-badge"
                initial={{ opacity: 0, scale: 0.5 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ delay: 0.2 }}
              >
                <span className="badge-dot"></span>
                Available for Work
              </motion.div>
              
              <motion.h1 
                className="gradient-text"
                variants={fadeInUp}
                initial="hidden"
                animate="visible"
              >
                Hi, I'm a Student & Developer üë®‚Äçüíª
              </motion.h1>
              
              <motion.p 
                className="typing-text"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.8 }}
              >
                {text}<span className="cursor-blink">|</span>
              </motion.p>
              
              <motion.div 
                className="hero-stats"
                initial="hidden"
                animate="visible"
                variants={{
                  visible: {
                    transition: {
                      staggerChildren: 0.1,
                      delayChildren: 1
                    }
                  }
                }}
              >
                {[
                  { number: '50+', label: 'Projects' },
                  { number: '30+', label: 'Clients' },
                  { number: '3+', label: 'Years Exp' }
                ].map((stat, index) => (
                  <motion.div 
                    key={index}
                    className="stat-box"
                    variants={scaleIn}
                  >
                    <h3>{stat.number}</h3>
                    <p>{stat.label}</p>
                  </motion.div>
                ))}
              </motion.div>
              
              <motion.div 
                className="cta-buttons"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 1.2 }}
              >
                <Link to="/projects" className="btn btn-primary">
                  <span>View My Work</span>
                  <span>‚Üí</span>
                </Link>
                <Link to="/contact" className="btn btn-secondary">
                  <span>Let's Talk</span>
                </Link>
              </motion.div>
            </motion.div>

            {/* Right: 3D Student Avatar */}
            <motion.div 
              className="hero-avatar"
              initial={{ opacity: 0, scale: 0.5, rotateY: -30 }}
              animate={{ opacity: 1, scale: 1, rotateY: 0 }}
              transition={{ duration: 1, delay: 0.5 }}
              style={{ y: y1 }}
            >
              <div className="avatar-container">
                {/* Rotating Rings */}
                <div className="avatar-ring ring-1"></div>
                <div className="avatar-ring ring-2"></div>
                <div className="avatar-ring ring-3"></div>
                
                {/* Student Character */}
                <div className="avatar-character">
                  <div className="avatar-head">
                    <div className="avatar-face">
                      <div className="avatar-eyes">
                        <div className="eye left"></div>
                        <div className="eye right"></div>
                      </div>
                      <div className="avatar-mouth"></div>
                    </div>
                    <div className="avatar-hair"></div>
                  </div>
                  <div className="avatar-body">
                    <div className="avatar-shirt"></div>
                    <div className="avatar-arms">
                      <div className="arm left"></div>
                      <div className="arm right"></div>
                    </div>
                  </div>
                </div>

                {/* Floating Icons */}
                <motion.div 
                  className="floating-icon icon-1"
                  animate={{ 
                    y: [0, -20, 0],
                    rotate: [0, 360]
                  }}
                  transition={{ 
                    duration: 3, 
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                >
                  üíª
                </motion.div>
                <motion.div 
                  className="floating-icon icon-2"
                  animate={{ 
                    y: [0, -15, 0],
                    rotate: [0, -360]
                  }}
                  transition={{ 
                    duration: 4, 
                    repeat: Infinity,
                    ease: "easeInOut",
                    delay: 0.5
                  }}
                >
                  üìö
                </motion.div>
                <motion.div 
                  className="floating-icon icon-3"
                  animate={{ 
                    y: [0, -25, 0],
                  }}
                  transition={{ 
                    duration: 3.5, 
                    repeat: Infinity,
                    ease: "easeInOut",
                    delay: 1
                  }}
                >
                  üéì
                </motion.div>
              </div>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Skills Section with Scroll Animation */}
      <section className="skills-section">
        <div className="container">
          <motion.div 
            className="section-header"
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.3 }}
            variants={fadeInUp}
          >
            <span className="section-label">What I Do</span>
            <h2>My Expertise</h2>
          </motion.div>
          
          <div className="skills-grid">
            {[
              { icon: 'üíª', title: 'Frontend', desc: 'React, Next.js, JavaScript' },
              { icon: '‚öôÔ∏è', title: 'Backend', desc: 'Node.js, Express, MongoDB' },
              { icon: 'üé®', title: 'UI/UX', desc: 'Figma, Adobe XD' },
              { icon: 'üöÄ', title: 'Deployment', desc: 'Vercel, Netlify, AWS' }
            ].map((skill, index) => (
              <motion.div 
                key={index}
                className="skill-card"
                initial="hidden"
                whileInView="visible"
                viewport={{ once: true, amount: 0.3 }}
                variants={scaleIn}
                transition={{ delay: index * 0.1 }}
                whileHover={{ 
                  scale: 1.05,
                  transition: { duration: 0.3 }
                }}
              >
                <div className="skill-icon">{skill.icon}</div>
                <h3>{skill.title}</h3>
                <p>{skill.desc}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Projects Section with Scroll Reveal */}
      <section className="featured-projects-section">
        <div className="container">
          <motion.div 
            className="section-header"
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.3 }}
            variants={fadeInUp}
          >
            <span className="section-label">Portfolio</span>
            <h2>Featured Projects</h2>
          </motion.div>
          
          <div className="projects-showcase">
            {[
              {
                img: 'https://images.unsplash.com/photo-1460925895917-afdab827c52f?w=800&q=80',
                category: 'Web Development',
                title: 'Analytics Dashboard',
                desc: 'Real-time data visualization with interactive charts',
                tags: ['React', 'D3.js', 'Node.js']
              },
              {
                img: 'https://images.unsplash.com/photo-1557821552-17105176677c?w=800&q=80',
                category: 'E-commerce',
                title: 'Online Store',
                desc: 'Full-featured shopping platform with payments',
                tags: ['MERN', 'Stripe', 'Redux']
              },
              {
                img: 'https://images.unsplash.com/photo-1551650975-87deedd944c3?w=800&q=80',
                category: 'Social Media',
                title: 'Social Connect',
                desc: 'Modern social platform with real-time features',
                tags: ['React', 'Socket.io', 'MongoDB']
              }
            ].map((project, index) => (
              <motion.div 
                key={index}
                className="project-item"
                initial="hidden"
                whileInView="visible"
                viewport={{ once: true, amount: 0.2 }}
                variants={index % 2 === 0 ? fadeInLeft : fadeInRight}
                transition={{ delay: index * 0.1 }}
                whileHover={{ 
                  y: -15,
                  transition: { duration: 0.3 }
                }}
              >
                <div className="project-thumbnail">
                  <motion.img 
                    src={project.img}
                    alt={project.title}
                    loading="lazy"
                    whileHover={{ scale: 1.1 }}
                    transition={{ duration: 0.5 }}
                  />
                  <div className="project-overlay">
                    <Link to="/projects" className="view-project-btn">
                      View Project ‚Üí
                    </Link>
                  </div>
                </div>
                <div className="project-info">
                  <div className="project-category">{project.category}</div>
                  <h3>{project.title}</h3>
                  <p>{project.desc}</p>
                  <div className="project-tags">
                    {project.tags.map((tag, i) => (
                      <span key={i}>{tag}</span>
                    ))}
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
          
          <motion.div 
            className="view-all-center"
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
            variants={fadeInUp}
          >
            <Link to="/projects" className="btn btn-primary btn-large">
              View All Projects
            </Link>
          </motion.div>
        </div>
      </section>

      {/* Stats Section */}
      <motion.section 
        className="stats-section"
        style={{ y: y2 }}
      >
        <div className="container">
          <div className="stats-grid">
            {[
              { number: '50+', label: 'Projects Completed' },
              { number: '30+', label: 'Happy Clients' },
              { number: '3+', label: 'Years Experience' },
              { number: '100%', label: 'Satisfaction Rate' }
            ].map((stat, index) => (
              <motion.div 
                key={index}
                className="stat-item"
                initial="hidden"
                whileInView="visible"
                viewport={{ once: true }}
                variants={scaleIn}
                transition={{ delay: index * 0.1 }}
              >
                <h3 className="stat-number">{stat.number}</h3>
                <p>{stat.label}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </motion.section>

      {/* CTA Section */}
      <motion.section 
        className="cta-section"
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
        variants={fadeInUp}
      >
        <div className="container">
          <div className="cta-content">
            <h2>Let's Build Something Amazing</h2>
            <p>Have a project in mind? Let's discuss how I can help!</p>
            <div className="cta-buttons">
              <Link to="/contact" className="btn btn-primary btn-large">
                Start a Project
              </Link>
              <a href="mailto:your@email.com" className="btn btn-secondary btn-large">
                Email Me
              </a>
            </div>
          </div>
        </div>
      </motion.section>
    </div>
  );
};

export default Home;
