import React, { Suspense, useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, useGLTF, PerspectiveCamera, Environment } from '@react-three/drei';

// 3D Student Model Component
function StudentModel({ url }) {
  const { scene } = useGLTF(url || 'https://models.readyplayer.me/66d2b1bb8e5d3e3c83f96f37.glb');
  const modelRef = useRef();

  // Animate model (floating + rotation)
  useFrame((state) => {
    if (modelRef.current) {
      modelRef.current.rotation.y = Math.sin(state.clock.elapsedTime * 0.5) * 0.3;
      modelRef.current.position.y = Math.sin(state.clock.elapsedTime * 0.8) * 0.1;
    }
  });

  return (
    <primitive 
      ref={modelRef}
      object={scene} 
      scale={2}
      position={[0, -1.5, 0]}
    />
  );
}

// Main Avatar Component
export default function Avatar3D() {
  return (
    <div style={{ width: '100%', height: '500px' }}>
      <Canvas>
        {/* Camera */}
        <PerspectiveCamera makeDefault position={[0, 0, 5]} />
        
        {/* Lighting */}
        <ambientLight intensity={0.5} />
        <directionalLight position={[10, 10, 5]} intensity={1} />
        <pointLight position={[-10, -10, -5]} intensity={0.5} />
        
        {/* Environment */}
        <Environment preset="city" />
        
        {/* 3D Model */}
        <Suspense fallback={null}>
          <StudentModel />
        </Suspense>
        
        {/* Controls */}
        <OrbitControls 
          enableZoom={false}
          enablePan={false}
          minPolarAngle={Math.PI / 2.5}
          maxPolarAngle={Math.PI / 2.5}
        />
      </Canvas>
    </div>
  );
}
