import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectCard from '../components/ProjectCard';
import AOS from 'aos';
import 'aos/dist/aos.css';

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // AOS initialize karo
    AOS.init({
      duration: 1000,
      once: false,
    });

    const fetchProjects = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/projects');
        setProjects(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching projects:', error);
        setError('Failed to load projects');
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  if (loading) {
    return (
      <div className="projects-page">
        <div className="loader">Loading projects...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="projects-page">
        <div className="container">
          <h1>My Projects</h1>
          <p className="error-message">{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="projects-page">
      <section className="projects-section">
        <div className="container">
          {/* Heading mein animation */}
          <h1 data-aos="zoom-in">My Projects</h1>
          
          {/* Paragraph mein animation */}
          <p data-aos="fade-up" data-aos-delay="200">
            Explore my latest work and creative solutions
          </p>
          
          {/* Projects grid mein animations */}
          <div className="projects-grid">
            {projects.length === 0 ? (
              <p>No projects available yet. Add some from the backend!</p>
            ) : (
              projects.map((project, index) => (
                // Har project card ko animation do
                <div 
                  key={project._id} 
                  data-aos="fade-up" 
                  data-aos-delay={index * 100}
                >
                  <ProjectCard project={project} />
                </div>
              ))
            )}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Projects;
