import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './VisitorCounter.css';

const VisitorCounter = () => {
  const [stats, setStats] = useState({
    visitors: 0,
    countries: 0,
    projects: 3
  });

  useEffect(() => {
    // Get visitor count from localStorage
    const currentCount = parseInt(localStorage.getItem('visitorCount') || '12543');
    const newCount = currentCount + 1;
    localStorage.setItem('visitorCount', newCount.toString());

    // Animate counting
    const timer = setTimeout(() => {
      setStats({
        visitors: newCount,
        countries: 52,
        projects: 3
      });
    }, 500);

    return () => clearTimeout(timer);
  }, []);

  const counterVariants = {
    hidden: { opacity: 0, scale: 0.5 },
    visible: { 
      opacity: 1, 
      scale: 1,
      transition: { duration: 0.5 }
    }
  };

  return (
    <motion.div 
      className="visitor-counter"
      initial="hidden"
      animate="visible"
      variants={counterVariants}
    >
      <div className="counter-item">
        <span className="counter-icon">üëÄ</span>
        <div className="counter-details">
          <span className="counter-number">
            {stats.visitors.toLocaleString()}
          </span>
          <span className="counter-label">Total Visitors</span>
        </div>
      </div>

      <div className="counter-divider"></div>

      <div className="counter-item">
        <span className="counter-icon">üåç</span>
        <div className="counter-details">
          <span className="counter-number">{stats.countries}+</span>
          <span className="counter-label">Countries</span>
        </div>
      </div>

      <div className="counter-divider"></div>

      <div className="counter-item">
        <span className="counter-icon">üöÄ</span>
        <div className="counter-details">
          <span className="counter-number">{stats.projects}</span>
          <span className="counter-label">Live Projects</span>
        </div>
      </div>
    </motion.div>
  );
};

export default VisitorCounter;
